function writeMonth(){monthFull=daToday.toLocaleString(loc,{month:"long"}),$("#title_month").html(monthFull)}function writeDaysOfWeek(){for(var t=0;7>t;t++){var a=daToday,o=daToday.toLocaleString(loc,{weekday:"short"});$("#"+o).html(a.getDate()),"Sat"==o?a.addDays(-6):a.addDays(1)}}var daToday=new Date.now,loc="en-US";writeMonth(),writeDaysOfWeek();